import React, { useState } from 'react';

const DishSuggester = () => {
  const [ingredients, setIngredients] = useState('');
  const [suggestions, setSuggestions] = useState([]);

  const handleSearch = () => {
    fetch(`http://localhost:3000/dishes/suggest?ingredients=${ingredients}`) // Replace with your backend API
      .then((response) => response.json())
      .then((data) => setSuggestions(data));
  };

  return (
    <div>
      <h1>Dish Suggester</h1>
      <input
        type="text"
        value={ingredients}
        onChange={(e) => setIngredients(e.target.value)}
        placeholder="Enter ingredients (comma separated)"
      />
      <button onClick={handleSearch}>Suggest Dishes</button>
      <ul>
        {suggestions.map((dish, index) => (
          <li key={index}>{dish.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default DishSuggester;
