import React, { useState, useEffect } from 'react';
import { fetchDishes } from './api';
import DishList from './components/DishList';
import './App.css';

function App() {
  const [dishes, setDishes] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    fetchDishes().then(data => setDishes(data));
  }, []);

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
  };

  const filteredDishes = dishes.filter(dish =>
    dish.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    dish.ingredients.toLowerCase().includes(searchQuery.toLowerCase()) ||
    dish.state.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="app-container">
      <header className="header">
        <h1>Indian Cuisine Dish Explorer</h1>
        <input 
          type="text" 
          placeholder="Search dishes..." 
          value={searchQuery} 
          onChange={handleSearch} 
          className="search-bar" 
        />
      </header>
      <DishList dishes={filteredDishes} />
    </div>
  );
}

export default App;
