import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const DishList = () => {
  const [dishes, setDishes] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredDishes, setFilteredDishes] = useState([]);

   // Pagination limit
  const itemsPerPage = 10;
  const totalPages = Math.ceil(dishes.length / itemsPerPage);
  const [page, setPage] = useState(1);
  
  const displayedDishes = dishes.slice((page - 1) * itemsPerPage, page * itemsPerPage);
  
  const handlePageChange = (newPage) => {
    setPage(newPage);
  };
  
  <div className="pagination">
    {[...Array(totalPages)].map((_, index) => (
      <button
        key={index}
        onClick={() => handlePageChange(index + 1)}
        className={index + 1 === page ? 'active' : ''}
      >
        {index + 1}
      </button>
    ))}
  </div>
  
  useEffect(() => {
    fetch('http://localhost:3000/dishes') // Replace with your API endpoint
      .then((response) => response.json())
      .then((data) => {
        setDishes(data);
        setFilteredDishes(data);
      });
  }, []);

  // Handle filtering by search term
  useEffect(() => {
    const results = dishes.filter(
      (dish) =>
        dish.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        dish.state.toLowerCase().includes(searchTerm.toLowerCase()) ||
        dish.diet.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredDishes(results);
  }, [searchTerm, dishes]);

  // Pagination calculations
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentDishes = filteredDishes.slice(indexOfFirstItem, indexOfLastItem);

  return (
    <div className="dish-list-container">
       <table className="dish-list-table">
       
      
      <h1>Dish List</h1>
      <input
        type="text"
        placeholder="Search..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <table border="1">
        <thead>
          <tr>
            <th>Name</th>
            <th>Preparation Time</th>
            <th>Cooking Time</th>
            <th>Diet</th>
            <th>State</th>
          </tr>
          
        </thead>
        <tbody>
          {currentDishes.map((dish) => (
            <tr key={dish.name}>
              <td>
                <Link to={`/dish/${dish.name}`}>{dish.name}</Link>
              </td>
              <td>{dish.prep_time} mins</td>
              <td>{dish.cook_time} mins</td>
              <td>{dish.diet}</td>
              <td>{dish.state}</td>
            </tr>
          ))}
        </tbody>
      </table>
      </table>
      <div>
        {Array.from(
          { length: Math.ceil(filteredDishes.length / itemsPerPage) },
          (_, index) => (
            <button key={index} onClick={() => setCurrentPage(index + 1)}>
              {index + 1}
            </button>
          )
        )}
      </div>
    </div>
  );
};

export default DishList;
