import React, { useState, useEffect } from 'react';

const DishDetails = ({ match }) => {
  const [dish, setDish] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:3000/dishes/${match.params.name}`)
      .then((response) => response.json())
      .then((data) => setDish(data));
  }, [match.params.name]);

  if (!dish) return <div>Loading...</div>;

  return (
    <div>
      <h1>{dish.name}</h1>
      <p>Ingredients: {dish.ingredients}</p>
      <p>Diet: {dish.diet}</p>
      <p>Prep Time: {dish.prep_time} minutes</p>
      <p>Cooking Time: {dish.cooking_time} minutes</p>
      <p>Flavor: {dish.flavor}</p>
      <p>Region: {dish.region}</p>
    </div>
  );
};

export default DishDetails;
