// src/components/DishList.js

import React, { useState, useEffect } from 'react';
import { TextField, PrimaryButton, DetailsList, DetailsListLayoutMode, DetailsRow } from '@fluentui/react';

const DishList = () => {
  const [dishes, setDishes] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredDishes, setFilteredDishes] = useState([]);

  const itemsPerPage = 5; // Pagination limit
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    // Fetch all dishes when the component mounts
    fetch('http://localhost:3000/dishes')
      .then((response) => response.json())
      .then((data) => {
        setDishes(data);
        setFilteredDishes(data);
      });
  }, []);

  // Handle searching and filtering dishes
  useEffect(() => {
    const results = dishes.filter(
      (dish) =>
        dish.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        dish.state.toLowerCase().includes(searchTerm.toLowerCase()) ||
        dish.diet.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredDishes(results);
  }, [searchTerm, dishes]);

  // Handle pagination
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentDishes = filteredDishes.slice(indexOfFirstItem, indexOfLastItem);

  const handleSearch = () => {
    // Search functionality is handled by state change
  };

  const columns = [
    { key: 'name', name: 'Dish Name', fieldName: 'name', minWidth: 100, maxWidth: 200 },
    { key: 'ingredients', name: 'Ingredients', fieldName: 'ingredients', minWidth: 100, maxWidth: 200 },
    { key: 'diet', name: 'Diet Type', fieldName: 'diet', minWidth: 100, maxWidth: 200 },
    { key: 'prep_time', name: 'Preparation Time', fieldName: 'prep_time', minWidth: 100, maxWidth: 150 },
    { key: 'cook_time', name: 'Cooking Time', fieldName: 'cook_time', minWidth: 100, maxWidth: 150 },
    { key: 'flavor', name: 'Flavor', fieldName: 'flavor', minWidth: 100, maxWidth: 150 },
    { key: 'course', name: 'Course', fieldName: 'course', minWidth: 100, maxWidth: 150 },
    { key: 'state', name: 'State', fieldName: 'state', minWidth: 100, maxWidth: 150 },
    { key: 'region', name: 'Region', fieldName: 'region', minWidth: 100, maxWidth: 150 },
  ];

  return (
    <div>
      <h1>Dish List</h1>

      {/* Fluent UI TextField for input */}
      <TextField
        label="Search Dishes"
        value={searchTerm}
        onChange={(e, newValue) => setSearchTerm(newValue)}
        placeholder="Type dish name, ingredients, or origin"
      />

      {/* Fluent UI PrimaryButton */}
      <PrimaryButton onClick={handleSearch}>Search</PrimaryButton>

      {/* Fluent UI DetailsList for displaying dishes in a table */}
      <DetailsList
        items={currentDishes}
        columns={columns}
        setKey="set"
        layoutMode={DetailsListLayoutMode.fixedColumns}
        onRenderRow={(props) => <DetailsRow {...props} />}
      />

      {/* Pagination buttons */}
      <div>
        {Array.from({ length: Math.ceil(filteredDishes.length / itemsPerPage) }, (_, index) => (
          <button key={index} onClick={() => setCurrentPage(index + 1)}>
            {index + 1}
          </button>
        ))}
      </div>
    </div>
  );
};

export default DishList;
