import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function DishDetails() {
  const { name } = useParams();
  const [dish, setDish] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:3001/dishes/${name}`)
      .then((response) => response.json())
      .then((data) => setDish(data))
      .catch((error) => console.error('Error fetching dish details:', error));
  }, [name]);

  if (!dish) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>{dish.name}</h2>
      <p><strong>Ingredients:</strong> {dish.ingredients}</p>
      <p><strong>Diet:</strong> {dish.diet}</p>
      <p><strong>Preparation Time:</strong> {dish.prepTime}</p>
      <p><strong>Cooking Time:</strong> {dish.cookingTime}</p>
      <p><strong>Flavor:</strong> {dish.flavor}</p>
      <p><strong>Course:</strong> {dish.course}</p>
      <p><strong>State:</strong> {dish.state}</p>
      <p><strong>Region:</strong> {dish.region}</p>
    </div>
  );
}

export default DishDetails;
