import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const DishList = () => {
  const [dishes, setDishes] = useState([]);  // Initialize as an empty array
  const [filteredDishes, setFilteredDishes] = useState([]);  // Filtered dishes for search functionality
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const itemsPerPage = 5;
  const [isLoading, setIsLoading] = useState(true);  // Loading state for async fetch

  useEffect(() => {
    fetch('http://localhost:3000/dishes')
      .then((response) => response.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setDishes(data);  // Set dishes if data is an array
          setFilteredDishes(data);  // Set filtered dishes initially
          setIsLoading(false);  // Set loading to false after data is fetched
        } else {
          console.error('Fetched data is not an array:', data);
          setIsLoading(false);
        }
      })
      .catch((error) => {
        console.error('Error fetching dishes:', error);
        setIsLoading(false);
      });
  }, []);

  useEffect(() => {
    if (Array.isArray(dishes)) {
      const results = dishes.filter(
        (dish) =>
          dish.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          dish.state.toLowerCase().includes(searchTerm.toLowerCase()) ||
          dish.diet.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredDishes(results);  // Update filteredDishes with search results
    }
  }, [searchTerm, dishes]);

  if (isLoading) {
    return <p>Loading...</p>;  // Show loading message while fetching data
  }

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentDishes = filteredDishes.slice(indexOfFirstItem, indexOfLastItem);

  return (
    <div>
      <h1>Dish List</h1>
      <input
        type="text"
        placeholder="Search..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <table border="1">
        <thead>
          <tr>
            <th>Name</th>
            <th>Preparation Time</th>
            <th>Cooking Time</th>
            <th>Diet</th>
            <th>State</th>
          </tr>
        </thead>
        <tbody>
          {currentDishes.map((dish) => (
            <tr key={dish.name}>
              <td>
                <Link to={`/dish/${dish.name}`}>{dish.name}</Link>
              </td>
              <td>{dish.prep_time} mins</td>
              <td>{dish.cook_time} mins</td>
              <td>{dish.diet}</td>
              <td>{dish.state}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div>
        {Array.from(
          { length: Math.ceil(filteredDishes.length / itemsPerPage) },
          (_, index) => (
            <button key={index} onClick={() => setCurrentPage(index + 1)}>
              {index + 1}
            </button>
          )
        )}
      </div>
    </div>
  );
};

export default DishList;
