// src/components/DishDetails.js

import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // For accessing dynamic route parameters
import { Text } from '@fluentui/react';

const DishDetails = () => {
  const { name } = useParams(); // Extract the dish name from the URL
  const [dish, setDish] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Ensure the name is passed correctly and trim any accidental whitespace
    const trimmedName = name.trim();

    fetch(`http://localhost:3000/dishes/${encodeURIComponent(trimmedName)}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Failed to fetch dish details: ${response.statusText}`);
        }
        return response.json();
      })
      .then((data) => setDish(data))
      .catch((err) => setError(err.message));
  }, [name]);

  if (error) return <p>Error: {error}</p>;
  if (!dish) return <p>Loading...</p>;

  return (
    <div>
      <h1>{dish.name}</h1>
      <Text><strong>Ingredients:</strong> {dish.ingredients}</Text>
      <Text><strong>Diet Type:</strong> {dish.diet}</Text>
      <Text><strong>Preparation Time:</strong> {dish.prep_time} mins</Text>
      <Text><strong>Cooking Time:</strong> {dish.cook_time} mins</Text>
      <Text><strong>Flavor:</strong> {dish.flavor}</Text>
      <Text><strong>Course:</strong> {dish.course}</Text>
      <Text><strong>State:</strong> {dish.state}</Text>
      <Text><strong>Region:</strong> {dish.region}</Text>
    </div>
  );
};

export default DishDetails;
